mapper.select:
		select 
   			 r.reply_no, r.member_id, r.reply_content, r.reply_date, r.reply_grade 
   			 from (
   			 	select rownum rn, a.* 
   					 from (
    					select * from m_reply where movie_no = #{movieNo} order by reply_date desc)a
    					   )r
    			where rn between #{start} and #{end}
